import "../../assets/sass/sass-components/login-page/login-page.scss";

import React, { useEffect, useRef, useState } from "react";
import { useNavigate } from "react-router-dom";
import Axios from "axios";

import BackgroundContainer from "../container-components/background-container";
import NavbarContainer from "../container-components/navbar-container";
import hasJWT from "../../auth/hasJWT";

function LoginPage() {
  const emailRef = useRef();

  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  let navigate = useNavigate();

  const routeAuth = () => {
    if (hasJWT()) {
      navigate("/user/panel");
    } else {
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    try {
      const response = Axios.post(
        "http://localhost:3001/login",
        JSON.stringify({ email: email, password: password }),
        {
          headers: { "Content-Type": "application/json" },
        }
      );
      Axios.interceptors.response.use(
        function (response) {
          if (response.data.status === "ok") {
            console.log(response.data.token);
            alert("Login success");
            localStorage.setItem("token", response.data.token);
            console.log(response.data.token);
            navigate("/user/panel");
          } else if (hasJWT()) {
            alert(false);
          } else {
            alert("login failed");
          }
        },
        function (error) {
          return Promise.reject(error);
        }
      );
      setEmail("");
      setPassword("");
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    routeAuth();
    emailRef.current.focus();
  }, []);

  return (
    <div className="login-page-container">
      <NavbarContainer navCurrentPath="/Login" />
      <form method="post" onSubmit={handleSubmit}>
        <div className="login-inputcontainer">
          <label htmlFor="email-input">Enter your email :D</label>
          <input
            className="inputborder"
            id="email-input"
            type="email"
            ref={emailRef}
            onChange={(e) => setEmail(e.target.value)}
            value={email}
            required
          ></input>
          <label htmlFor="password-input">Enter your password :V</label>
          <input
            className="inputborder"
            id="password-input"
            type="password"
            onChange={(e) => setPassword(e.target.value)}
            value={password}
            required
          ></input>
          <input id="login-submitbtn" type="submit" value="Login"></input>
        </div>
      </form>
      <BackgroundContainer />
    </div>
  );
}

export default LoginPage;
