    //   console.log("Success", response.data);
    //   setEmail("");
    //   setPassword("");
    //   setSuccess(true);
    // } catch (error) {
    //   console.log("Error", error);
    // }

    const Jsondata = {
      email: email,
      password: password,
    };
    fetch("http://localhost:3001/login", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(Jsondata),
    })
      .then(console.log(response))
      .then((response) => response.json())
      .then((data) => {
        if (data.status === "ok") {
          alert("Success");
          localStorage.setItem("token", data.token);
          window.location = "/user/panel";
        } else {
          alert("Login failed");
        }
        console.log("Success", data);
      })
      .catch((error) => {
        console.log("Error", error);
      });
  };

  return (
    <div className="login-page-container">
      <NavbarContainer navCurrentPath="/Login" />
      <form action="" method="post" onSubmit={handleSubmit}>
        <div className="login-inputcontainer">
          <label htmlFor="email-input">Enter your email :D</label>
          <input
            className="inputborder"
            id="email-input"
            type="email"
            ref={emailRef}
            onChange={(e) => setEmail(e.target.value)}
            value={email}
            required
          ></input>
          <label htmlFor="password-input">Enter your password :V</label>
          <input
            className="inputborder"
            id="password-input"
            type="password"
            onChange={(e) => setPassword(e.target.value)}
            value={password}
            required
          ></input>
          <input id="login-submitbtn" type="submit" value="Login"></input>
        </div>
      </form>
      <BackgroundContainer />
    </div>
  );
}

export default LoginPage;
