import "../../assets/sass/sass-components/container-component/navbar-container.scss";

import React, { useState,useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";

import getUserauth from "../../auth/getUserauth";
import hasJWT from "../../auth/hasJWT";

function NavbarContainer(probs) {
  let navigate = useNavigate();
  const [username, setUsername] = useState(null);

  const routeAuth = () => {
    if (hasJWT()) {
      getUserauth().then((result) => {
        setUsername(result.data.decoded.username);
      });
    } else {
      setUsername("Anomymous kun");
    }
  };

  const [userloginpanel, setUserLoginpanel] = useState(
    <Link
      className={
        window.location.pathname === "/"
          ? "nav-text-id3"
          : window.location.pathname === "/login"
          ? "nav-text-id3"
          : window.location.pathname === "/register"
          ? "nav-text-id3"
          : window.location.pathname === "/user/panel"
          ? "nav-text-id3_user-panel"
          : ""
      }
      to="/login"
    >
      Login
    </Link>
  );
  const [userregisterpanel, setUserRegisterpanel] = useState(
    <Link
      className={
        window.location.pathname === "/"
          ? "nav-text-id4"
          : window.location.pathname === "/login"
          ? "nav-text-id4"
          : window.location.pathname === "/register"
          ? "nav-text-id4"
          : window.location.pathname === "/user/panel"
          ? "nav-text-id4_user-panel"
          : ""
      }
      to="/register"
    >
      Register
    </Link>
  );

  useEffect(() => {
    routeAuth();
  }, []);

  return (
    // <nav className="nav-container">
    <nav
      className={
        window.location.pathname === "/"
          ? "nav-container"
          : window.location.pathname === "/login"
          ? "nav-container_login"
          : window.location.pathname === "/register"
          ? "nav-container_register"
          : window.location.pathname === "/user/panel"
          ? "nav-container_user-panel"
          : ""
      }
    >
      <img
        id="homebtn"
        src="/images/home.png"
        onClick={() => {
          window.location = "/";
        }}
      ></img>
      <div className="nav-texts">
        <div id="nav-text-id1">MM16z</div>
        <div id="nav-text-id2">Town</div>
        {/* <div id="nav-text-id5">{probs.navCurrentPath}</div> */}
        <div
          className={
            window.location.pathname === "/"
              ? "nav-text-id5"
              : window.location.pathname === "/login"
              ? "nav-text-id5_login-page"
              : window.location.pathname === "/register"
              ? "nav-text-id5_register-page"
              : window.location.pathname === "/user/panel"
              ? "nav-text-id5_user-panel-page"
              : ""
          }
        >
          
        </div>
        {userloginpanel}
        {userregisterpanel}
      </div>
    </nav>
  );
}

export default NavbarContainer;
