import "../../assets/sass/sass-components/login-page/login-page.scss";

import React, { useEffect, useRef, useState } from "react";
import Axios from "axios";

import BackgroundContainer from "../container-components/background-container";
import NavbarContainer from "../container-components/navbar-container";

function LoginPage() {
  const emailRef = useRef();

  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [success, setSuccess] = useState(false);

  useEffect(() => {
    emailRef.current.focus();
  }, []);
  // const Jsondata = {
  //   email: email,
  //   password: password,
  // };
  // fetch("http://localhost:3001/login", {
  //   method: "POST",
  //   headers: {
  //     "Content-Type": "application/json",
  //   },
  //   body: JSON.stringify(Jsondata),
  // })
  //   .then((response) => response.json())
  //   .then((data) => {
  //     if (data.status === "ok") {
  //       alert("Success");
  //       localStorage.setItem("token", data.token);
  //       window.location = "/user/panel";
  //     } else {
  //       alert("Login failed");
  //     }
  //     console.log("Success", data);
  //   })
  //   .catch((error) => {
  //     console.log("Error", error);
  //   });

  const handleSubmit = (e) => {
    e.preventDefault();

    try {
      Axios.post(
        "http://localhost:3001/login",
        JSON.stringify({ email: email, password: password }),
        {
          headers: { "Content-Type": "application/json" },
        }
      );
      if (data.status === "ok") {
        alert("Success");
        localStorage.setItem("token", response.data.token);
        window.location = "/user/panel";
      } else {
        alert("Login failed");
      }
      console.log("Success", response.data);
      setEmail("");
      setPassword("");
      setSuccess(true);
    } catch (error) {
      console.log("Error", error);
    }
  };

  return (
    <div className="login-page-container">
      <NavbarContainer navCurrentPath="/Login" />
      <form action="" method="post" onSubmit={handleSubmit}>
        <div className="login-inputcontainer">
          <label htmlFor="email-input">Enter your email :D</label>
          <input
            className="inputborder"
            id="email-input"
            type="email"
            ref={emailRef}
            onChange={(e) => setEmail(e.target.value)}
            value={email}
            required
          ></input>
          <label htmlFor="password-input">Enter your password :V</label>
          <input
            className="inputborder"
            id="password-input"
            type="password"
            onChange={(e) => setPassword(e.target.value)}
            value={password}
            required
          ></input>
          <input id="login-submitbtn" type="submit" value="Login"></input>
        </div>
      </form>
      <BackgroundContainer />
    </div>
  );
}

export default LoginPage;
