import Axios from "axios";

const hasJWT = () => {
  const response = Axios.post(
    "http://localhost:3001/auth",
    JSON.stringify({}),
    {
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${localStorage.getItem("token")}`,
      },
    }
  )
    .then((response) => {
      if (response.data.status === "ok") {
        return true;
      } else {
        return false;
      }
    })
    .catch((error) => {
