import "../../assets/sass/sass-components/container-component/text-box-container.scss";

import React from "react";

function TextBoxContainer(probs) {
  
  const handleSubmit = (e) => {
    e.preventDefault();
    try {
      const response = Axios.post(
        "http://localhost:3001/login",
        JSON.stringify({ email: email, password: password }),
        {
          headers: { "Content-Type": "application/json" },
        }
      );
      Axios.interceptors.response.use(
        function (response) {
          if (response.data.status === "ok") {
            console.log(response.data.token);
            alert("Login success");
            localStorage.setItem("token", response.data.token);
            console.log(response.data.token);
            window.location = "/user/panel";
          } else {
            alert("login failed");
          }
        },
        function (error) {
          return Promise.reject(error);
        }
      );
      setEmail("");
      setPassword("");
    } catch (error) {
      console.log(error);
    }
  };

  const { username, context, date } = probs;
  return (
    <div className="text-box-container">
      <span
        id={
          window.location.pathname === "/"
            ? "text-box-from"
            : window.location.pathname === "/user/panel"
            ? "text-box-from_user-panel-page"
            : ""
        }
      >
        From : {username}
      </span>
      <input
        id={
          window.location.pathname === "/"
            ? "post-edit-btn_null"
            : window.location.pathname === "/user/panel"
            ? "post-edit-btn"
            : ""
        }
        type="button"
        value="EDIT"
      ></input>
      <span
        id={
          window.location.pathname === "/"
            ? "text-box-message"
            : window.location.pathname === "/user/panel"
            ? "text-box-message_user-panel-page"
            : ""
        }
      >
        {context}
      </span>
      <span
        id={
          window.location.pathname === "/"
            ? "text-box-date"
            : window.location.pathname === "/user/panel"
            ? "text-box-date_user-panel-page"
            : ""
        }
      >
        Date : {date}
      </span>
      <div
        className={
          window.location.pathname === "/"
            ? "text-box-border"
            : window.location.pathname === "/user/panel"
            ? "text-box-border_user-panel-page"
            : ""
        }
      ></div>
      <input
        id={
          window.location.pathname === "/"
            ? "post-delete-btn_null"
            : window.location.pathname === "/user/panel"
            ? "post-delete-btn"
            : ""
        }
        type="button"
        value="DELETE"
      ></input>
    </div>
  );
}

export default TextBoxContainer;
